class validPalindromeII
{
    /**
     * @param String $s
     * @return Boolean
     */
    function validPalindromeV2($s) {
        $i = 0;
        $j = strlen($s) - 1;
        while ($i <= $j) {
            if ($s[$i] === $s[$j]) {
                $i++;
                $j--;
            } else {
                // 将剩余的字符串拆分成两部分，看是否能够构成回文串
                return $this->validSubStrPalindromeV2($s, $i + 1, $j) || $this->validSubStrPalindromeV2($s, $i, $j - 1);
            }
        }

        return true;
    }

    private function validSubStrPalindromeV2($str, $i, $j)
    {
        while ($i <= $j) {
            if ($str[$i] !== $str[$j]) {
                return false;
            }
            $i++;
            $j--;
        }

        return true;
    }
}

$obj = new validPalindromeII();
$begin_time = time();
$str = '';

$ret = $obj->validPalindromeV2($str);
$end_time = time();
echo '共耗时：'.($end_time-$begin_time).' s';
var_dump($ret);